let place_parent = document.getElementById("destination");

function show_places(data) {
    place_parent.innerHTML = null
  data.forEach((element) => {
    let {name,img1,price ,Rating,duration} = element
    
// console.log(element);
    let div = document.createElement("div")
    div.classList.add("ds_places")
    div.addEventListener("click", ()=>{
        localStorage.setItem("placeDetails", JSON.stringify(element))
        window.location.href= "place_info.html"
    })
    div.innerHTML = `
    
    <div class="ds_image">
      <img
        src=${img1}
        alt=""
      />
    </div>
    <div class="ds_details">
      <h4>${name}</h4>
      <h3 class="${Rating >= 4 ? 'green': 'red'}">${Rating}</h3>
    </div>
    <div class="ds_loc">
      <h4>Duration: ${duration}</h4>
      <h4> Rs: ${price}</h4>
    </div>`

    place_parent.append(div)

  });
}

show_places(data);




// filter

let loc_fil = document.querySelector(".fil_by_loc")

loc_fil.addEventListener("change", ()=>{

    if(loc_fil.value == ""){
        show_places(data)
    }else{
        let filter_elem = data.filter((item)=>item.location == loc_fil.value)
        show_places(filter_elem)
    }
})


